parameters.functionCode:
const emailData = $items("Processing - Normalize Fields", 0, 0)[0].json;
const whitelistData = $items("Policy Layer - Get Whitelist");
const blacklistData = $items("Policy Layer - Get Blacklist");

// Robust extraction independent of column name
const whitelist = whitelistData
  .map(i => Object.values(i.json)[0]?.toLowerCase())
  .filter(Boolean);

const blacklist = blacklistData
  .map(i => Object.values(i.json)[0]?.toLowerCase())
  .filter(Boolean);

const domain = (emailData.emailDomain || "").toLowerCase();
const text = ((emailData.subject || "") + " " + (emailData.snippet || "")).toLowerCase();

let score = 0;
let matchedIndicators = 0;
let governanceTag = "Standard Monitoring";

// Whitelist check (supports subdomains)
if (whitelist.some(w => domain.endsWith(w))) {
  return [{ json: {
    domain,
    subject: emailData.subject,
    riskScore: 0,
    riskLevel: "Whitelisted",
    indicatorsMatched: 0,
    governanceTag: "Dynamic Whitelist",
    detectedAt: new Date().toISOString()
  }}];
}

// Blacklist check (supports subdomains)
if (blacklist.some(b => domain.endsWith(b))) {
  return [{ json: {
    domain,
    subject: emailData.subject,
    riskScore: 100,
    riskLevel: "Critical",
    indicatorsMatched: 0,
    governanceTag: "Dynamic Blacklist",
    detectedAt: new Date().toISOString()
  }}];
}

const highIndicators = {
  "api key": 40,
  "billing": 35,
  "payment": 35,
  "admin": 30
};

const mediumIndicators = {
  "trial": 20,
  "subscription": 15,
  "verify": 10,
  "activate": 15
};

for (const key in highIndicators) {
  if (text.includes(key)) {
    score += highIndicators[key];
    matchedIndicators++;
  }
}

for (const key in mediumIndicators) {
  if (text.includes(key)) {
    score += mediumIndicators[key];
    matchedIndicators++;
  }
}

// External domain penalty
if (!domain.endsWith("gmail.com") &&
    !domain.endsWith("outlook.com") &&
    !domain.endsWith("company.com")) {
  score += 15;
}

// Multiple indicator bonus
if (matchedIndicators >= 2) score += 10;

let riskLevel = "Low";

if (score >= 80) {
  riskLevel = "Critical";
  governanceTag = "Critical Governance Action";
}
else if (score >= 60) {
  riskLevel = "High";
  governanceTag = "Elevated Risk";
}
else if (score >= 30) {
  riskLevel = "Medium";
}

return [{ json: {
  domain,
  subject: emailData.subject,
  riskScore: score,
  riskLevel,
  indicatorsMatched: matchedIndicators,
  governanceTag,
  detectedAt: new Date().toISOString()
}}];

